import { listByRegistration } from '@/api/outpatient/feequery';
export default {
  name: 'Bill',
  props: ['patient'],
  watch: {
    'patient': function patient(newVal, oldVal) {
      this.patient = newVal;
      this.queryBill();
    }
  },
  data: function data() {
    return {
      bill: []
    };
  },
  created: function created() {},
  methods: {
    queryBill: function queryBill() {
      var _this = this;

      listByRegistration(this.patient.registrationId).then(function (res) {
        _this.bill = res.data;

        _this.bill.forEach(function (item) {
          item.totalprice = item.price * item.currentNum;
        });
      });
    }
  }
};
