import path from 'path'
import { deepClone } from '@/utils'
import { getSettleCatlist,getAllSettleCat,createSettleCat,deleteSettleCat,updateSettleCat } from '@/api/admin'
import Pagination from '@/components/Pagination' // Secondary package based on el-pagination
import { Promise } from 'q';

const defaultDepart = {
  id:'',
  code: '',
  name: '',
}

export default {
  components: {Pagination},
  data() {
    return {
      SettleCatList:[],
      SettleCat:{},
      depart: Object.assign({},defaultDepart),
      depList: [],
      dialogVisible: false,
      dialogType: 'new',
      checkStrictly: false,
      defaultProps: {
        children: 'children',
        label: 'title'
      },
      deptype:[1,2,3,4],
      total: 0,
      listLoading: true,
      allCatId:[],
      allDep:[],
      allType:[],
      listQuery: {
        id: '',
        code: '',
        name: '',
        status:'',
        page: 1,
        limit: 20
      },
      downloadLoading: false,
      rules:{
        name:[
          {required: true, message: '请输入结算类别名称',trigger: 'blur'}
        ]
      },
      deplistref:[]
    }
  },
  computed: {

  },
  created() {
    // Mock: get all routes and roles list from server
    this.getDeplist()
  },
  methods: {
    async getDeplist() {
      this.listLoading = true
      const response = await getSettleCatlist(this.listQuery)
      console.log(response)
      this.depList = response.data.list
      this.total = response.data.total
      this.listLoading = false
    },
    handleFilter() {
      this.listQuery.page = 1
      this.getDeplist()
    },
    handleAdd() {
      this.resetTemp()
      this.dialogType = 'new'
      this.dialogVisible = true
      this.checkStrictly = true
      this.$nextTick(() => {
        this.$refs['depart'].clearValidate()
      })
    },
    handleEdit(row) {
      this.resetTemp()
      this.depart = Object.assign({}, row) // copy obj
      this.dialogType = 'edit'
      this.dialogVisible = true
      this.checkStrictly = true
      this.$nextTick(() => {
        this.$refs['depart'].clearValidate()
      })
    },
    resetTemp() {
      this.depart.id=''
      this.depart.name=''
      this.depart.catId=''
      this.depart.code=''
      this.depart.type=''
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v => filterVal.map(j => {
        if (j === 'timestamp') {
          return parseTime(v[j])
        } else {
          return v[j]
        }
      }))
    },
    changedep(val){
      this.deplistref=val;
    },
    handleSomeDelete(){
      this.$confirm('确认删除选中结算类别?', '警告', {
        confirmButtonText: '确认',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(()=>{
      let delsom =''
      for(let i=0;i<this.deplistref.length;i++){
        if(i!==this.deplistref.length-1)
          delsom=delsom+(this.deplistref[i].id)+','
        else
          delsom=delsom+(this.deplistref[i].id)
      }

      console.log(delsom)
      deleteSettleCat(delsom).then(res=>{
      this.$notify({
        title: '成功',
        message: res.message,
        type: 'success',
        duration: 2000
      })
            this.getDeplist()
      }
      )
      })
    }
    ,
    handleDelete(row) {
      this.$confirm('确认删除当前结算类别?', '警告', {
        confirmButtonText: '确认',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(()=>{
      deleteSettleCat(row.id).then(res=>{
        this.$notify({
        title: '成功',
        message: '已删除该结算类别',
        type: 'success',
        duration: 2000
      })
      this.getDeplist()
      })
      })
    },
    async handleDownload() {
      let res = await getAllSettleCat()
      this.downloadLoading = true
      import('@/vendor/Export2Excel').then(excel => {
        const tHeader = ['编号', '结算类别名']
        const filterVal = ['id', 'name']
        const data = this.formatJson(filterVal, res.data)
        excel.export_json_to_excel({
          header: tHeader,
          data,
          filename: '结算类别'
        })
        this.downloadLoading = false
      })
    },
    async confirmDep(formName){
      this.$refs[formName].validate((valid)=>{
        if(valid){
          const isEdit = this.dialogType === 'edit'
          this.listLoading=true
          if(isEdit){
            console.log(this.depart.id)
            updateSettleCat(this.depart).then(res=>{
                this.getDeplist()
                this.$notify({
                title: '成功',
                message: '已修改该结算类别',
                type: 'success',
                duration: 2000
              })
            })
            this.dialogVisible=false
          }else{
            createSettleCat(this.depart).then(res=>{
              this.getDeplist()
              this.$notify({
              title: '成功',
              message: res.message,
              type: 'success',
              duration: 2000
            })
            this.dialogVisible=false
            })
          }
        }
      })
    },
  }
}
