import {create,select,update} from '@/api/user'
import {parseTime,deepClone} from '@/utils'
import {getAllDep} from '@/api/department'
import Pagination from '@/components/Pagination'
export default {
  components: {Pagination},
  data(){
    return{
      alldepart:[],
      user:{
        id:'',
        username:'',
        name:'',
        role:'',
        dept:'',
        deptname:''
      },
      total:0,
      userlist:[],
      listQuery:{
        deptId:'',
        gender:'',
        username:'',
        roleId:'',
        pageSize:10,
        pageNum:1
      },
      dialogType:'',
      dialogVisible:false
    }
  },
  created(){
    this.select()
    this.getAlldep()
  },
  methods:{
    async getAlldep(){
      const res = await getAllDep()
      this.alldepart=res.data
    },
    confirmUser(){
      if(this.dialogType === 'add'){
        this.user.deptId = this.user.dept
        this.user.roleId = this.user.role
        this.user.registrationRankId = 1
        this.user.gender = 0
        create(this.user).then(res=>{
          this.$notify({
            title: '成功',
            message: '成功新增用户',
            type: 'success',
            duration: 2000
          })
          this.select()
        })
        this.dialogVisible = false
      }
      else{
        this.user.deptId = this.user.dept
        this.user.roleId = this.user.role
        update(this.user).then(res=>{
          this.$notify({
            title: '成功',
            message: '修改成功',
            type: 'success',
            duration: 2000
          })
          this.select()
        })
        this.dialogVisible = false
      }
    },
    select(){
      select(this.listQuery).then(res=>{
        this.userlist = res.data.list
        this.total = res.data.total
        console.log(this.userlist)
        this.userlist.forEach(item=>{
          item.createTime = parseTime(item.createTime)
        })
      })
    },
    handleAddRole(){
      this.user.id = ''
      this.user.name = ''
      this.user.role = ''
      this.user.username = ''
      this.user.dept = ''
      this.dialogType = 'add'
      this.dialogVisible = true
    },
    handleEdit(row){
      this.dialogType = 'edit'
      this.user.id = row.id
      this.user.username = JSON.parse(JSON.stringify(row.username))
      this.user.name =JSON.parse(JSON.stringify(row.name))
      this.user.role = row.role.id
      this.user.dept = row.dept.id
      this.user.deptname = row.dept.name
      this.dialogVisible = true
      console.log(this.user)
    }
  }
}
