import CountTo from 'vue-count-to'
import {staffPatients,deptPatients,totalPatients} from '@/api/charts'
import {parseTime} from '@/utils'
export default {
  components: {
    CountTo
  },
  data(){
    return{
      date: '',
      num:0,
      depnum:0,
      totalnum:0
    }
  },
  created(){
    this.staffPatients()
    this.deptPatients()
    this.totalPatients()
    this.date = parseTime(new Date()).substr(0,10)
  },
  methods: {
    staffPatients(){
      this.num = 0
      staffPatients(this.$store.getters.id).then(res=>{
        res.data.numOfPatients.forEach(item=>{
          this.num += item
        })
      })
    },
    deptPatients(){
      this.deptnum = 0
      deptPatients(this.$store.getters.deptId).then(res=>{
        res.data.numOfPatients.forEach(item=>{
          this.deptnum += item
        })
      })
    },
    totalPatients(){
      this.totalnum = 0
      totalPatients().then(res=>{
        res.data.numOfPatients.forEach(item=>{
          this.totalnum += item
        })
      })
    },
    handleSetLineChartData(type) {
      this.$emit('handleSetLineChartData', type)
    }
  }
}
