INSERT INTO `sms_staff` VALUES (28, 'licunjiang', '$2a$10$Qr2gM.UgbXwXT6lDh5D6xeumfGeD3aEMg4JmgJ4Q4v7gGZ/ezRe/a', 1, '2020-10-30 21:47:06', 0, NULL, '李存江教授', '李存江', 3, 2, 1, '卒中、神经内科', '现系首都医科大学宣武医院神经内科资深专家，主任医师；首都医科大学神经病学研究所副所长，北京神经内科会诊中心主任。 中国卒中学会理事；中国卒中学会卒中康复分会副主任委员；中国医师协会神经内科分会神经康复副主任委员；中国老年保健医学研究会老年脑血管病分会常务委员；中华医学会神经病学分会肌萎缩侧索硬化协作组委员；中华医学会北京分会内科学委员会常委；中华医学会北京分会罕见病学会委员；中国医师协会渐冻人专家委员会委员。 担任北京医学杂志副总编辑；中华内科杂志编委；中华老年心脑血管病杂志编委；中华老年多器官杂志编委；中国卒中杂志编委；中国实用内科杂志常务编委。', 'http://shandongciguang.oss-cn-beijing.aliyuncs.com/1604065512791.jpg', 0.000000);
INSERT INTO `sms_staff` VALUES (29, 'ludehong', '$2a$10$2zGTmDVs1LPGvYeRQY11qek8dWYZGqAkt8maUdJz5mWmoYCUxyb2m', 1, '2020-10-30 21:48:31', 0, NULL, '卢德宏教授', '卢德宏', 5, 2, 1, '擅长神经系统肿瘤、脑血管病、神经系统感染性疾病、神经系统脱髓鞘疾病、神经系统退行性疾病及难治性癫痫等疑难病症的神经病理诊断。', '首都医科大学宣武医院病理科首席专家；病理学主任医师、教授、博士生导师。从事临床神经病理诊断、教学和科研40余年。 擅长神经系统肿瘤、脑血管病、神经系统感染性疾病、神经系统脱髓鞘疾病、神经系统退行性疾病及难治性癫痫等疑难病症的神经病理诊断。   现任中华医学会病理学分会 常委兼秘书长；中国抗癌协会神经肿瘤专委会常委；北京市抗癌协会神经肿瘤专委会副主任委员；中国医师协会北京病理医师分会副会长；北京市临床病理住院医师规范化培训专家委员会主任委员。 中华神经科杂志、中华病理学杂志等12种学术期刊编委。', 'http://shandongciguang.oss-cn-beijing.aliyuncs.com/1604065670681.jpg', 0.000000);
INSERT INTO `sms_staff` VALUES (30, 'weidongning', '$2a$10$YzJlQCvr99weSWrFEvX0H.dR3JYBpKANSa8QFr98PDBYJuvMUaHC6', 1, '2020-10-30 21:50:35', 0, NULL, '魏东宁教授', '魏东宁', 3, 2, 1, '老年心脑血管病、', '中国人民解放军总医院（北京301医院）第八医疗中心主任医师，教授；现任中央军委保健委员会专家；北京宣武医院、北京天坛医院、解放军总医院和海军总医院神经内科会诊中心专家。 中国人民解放军科学技术委员会神经内科专委会常委；解放军总参谋部神经内科-神经外科-医学心理学专委会主任委员；中华医学会神经病学分会免疫学组委员；中华医学会免疫分会委员；中华预防医学会卒中预防与控制专委会委员；北京医学会神经内科分会委员；北京医师协会神经内科医师分会委员；北京市脑卒中防治协会常委。 担任中华老年心脑血管病杂志；中华脑血管病杂志；中国卒中杂志和中华保健医学杂志编委。 发表学术论文100余篇；获得军队医疗成果二等奖（2013）和首都临床特色研究课题（2013）和军队科研基金（2014）。', 'http://shandongciguang.oss-cn-beijing.aliyuncs.com/1604065755108.jpg', 0.000000);
